
public class EoperatorsHandson {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 55;
		int j = 10;
		System.out.println(i+j); //addition 65
		System.out.println(i-j); //sub 45
		System.out.println(i*j); //mul 550
		System.out.println((double)i/j); //div 5.5
		System.out.println(i%j); //modulus 5
		System.out.println(i++); //pre inc 55
		System.out.println(++i); //post inc 57
		System.out.println(i);
	}

}
